mapper.select:
SELECT

FROM SRIG_MQ_SERVER a

WHERE a.ID = #{id}

SELECT

FROM SRIG_MQ_SERVER a


    a.DEL_FLAG = 0

        AND a.IP = #{ip}


        AND a.PORT = #{port}


        AND a.PROTOCOL = #{protocol}


        AND a.TYPE = #{type}




        ORDER BY ${page.orderBy}

SELECT

FROM SRIG_MQ_SERVER a





        ORDER BY ${page.orderBy}

mapper.update:
UPDATE SRIG_MQ_SERVER
SET DEL_FALG = 1
WHERE ID = #{id}
